import React from 'react'
import { NavLink } from 'react-router-dom'
import Timer from './Timer';

const Dropdown = ({ isOpen, toggle }) => {
    const MinSecs = { minutes: 10, seconds: 0 }
    return (
      <div
        className={
          isOpen
            ? 'grid grid-rows-4 text-center items-center bg-white shadow-sm'
            : 'hidden'
        }
        onClick={toggle}>
        <div>
        <NavLink to="/" className="pl-4">Trial lesson[Grade 1-3]</NavLink>
        <Timer className="p-4" MinSecs={MinSecs} />
        <button className="bg-red-500 ml-4 md:block hidden  hover:bg-red-700 text-white font-bold py-2 px-4 rounded">End Class</button>
        </div>


      </div>
    );
  };

  export default Dropdown;
